import React from "react";
import DeckGL from "@deck.gl/react";
import { StaticMap } from "react-map-gl";
import { LineLayer } from "@deck.gl/layers";
import DarkMap from "../data/map.json";

const layer = new LineLayer({
  id: "LineLayer",
  data: [
    {
      inbound: 72633,
      outbound: 74735,
      from: {
        name: "Hyderabad",
        coordinates: [78.4867, 17.385],
      },
      to: {
        name: "12th St. Oakland City Center (12TH)",
        coordinates: [-122.271604, 37.803664],
      },
    },
  ],

  // widthMaxPixels: Number.MAX_SAFE_INTEGER,
  // widthMinPixels: 0,
  // widthScale: 1,
  // widthUnits: 'pixels',

  /* props inherited from Layer class */

  autoHighlight: true,
  // coordinateOrigin: [0, 0, 0],
  // coordinateSystem: COORDINATE_SYSTEM.LNGLAT,
  // highlightColor: [0, 0, 128, 128],
  // modelMatrix: null,
  // opacity: 1,
  pickable: true,
  // visible: true,
  // wrapLongitude: false,
});

const layers = [
  new LineLayer({
    id: "friends",
    data: flightPaths,
    opacity: 0.8,
    getSourcePosition: (d) => d.start,
    getTargetPosition: (d) => d.end,
    getColor,
    getWidth,
    pickable: true,
  }),
];

let INITIAL_VIEW_STATE = {
  longitude: -122.4,
  latitude: 37.74,
  zoom: 11,
  maxZoom: 20,
  pitch: 30,
  bearing: 0,
};

const MAP_STYLE = DarkMap;

class Map extends React.Component {
  render() {
    return (
      <div>
        <DeckGL
          layers={layer}
          initialViewState={INITIAL_VIEW_STATE}
          controller={true}
          getTooltip={({ object }) =>
            object && `${object.from.name} to ${object.to.name}`
          }
        >
          <StaticMap
            reuseMaps
            mapStyle={MAP_STYLE}
            preventStyleDiffing={true}
            mapboxApiAccessToken="pk.eyJ1IjoiY29ubmVjdGluZ3dvcmxkIiwiYSI6ImNrd2l3anlzdDB3bTAycG1kYXVlYnZtaXAifQ.fsAb70tSq-vGoIwjXuqreg"
          />
        </DeckGL>
      </div>
    );
  }
}

export default Map;
