import React, { useState } from "react";
import DeckGL from "@deck.gl/react";
import { StaticMap } from "react-map-gl";
import { LineLayer, ScatterplotLayer } from "@deck.gl/layers";
import DarkMap from "../data/map.json";

const friendshipData = [
  {
    start: [78.4867, 17.385],
    end: [8.6875, 54.2501],
    fromName: "Hyderabad",
    toName: "Denmark",
  },
  {
    start: [78.4867, 17.385],
    end: [13.4149, 46.8265],
    fromName: "Hyderabad",
    toName: "Austria",
  },
];

const peopleData = [
  {
    type: "mid",
    name: "Pranesh",
    coordinates: [78.4867, 17.385],
  },
  {
    type: "major",
    name: "Vamshi",
    coordinates: [8.6875, 54.2501],
  },
  {
    type: "major",
    name: "Mahesh",
    coordinates: [13.4149, 46.8265],
  },
];

let INITIAL_VIEW_STATE = {
  longitude: 78.4867,
  latitude: 17.385,
  zoom: 5,
  maxZoom: 20,
  pitch: 30,
  bearing: 0,
};

const MAP_STYLE = DarkMap;

const Map = () => {
  const [viewState, updateViewState] = useState(INITIAL_VIEW_STATE);

  let getSize = (zoom) => {
    return (23 - zoom) * 200;
  };

  const layers = [
    new LineLayer({
      id: "friendship",
      data: friendshipData,
      opacity: 0.8,
      getSourcePosition: (d) => d.start,
      getTargetPosition: (d) => d.end,
      getColor: [225, 0, 0, 123],
      getWidth: 5,
      pickable: true,
    }),
    new ScatterplotLayer({
      id: "person",
      data: peopleData,
      radiusScale: 10,
      getPosition: (d) => d.coordinates,
      getFillColor: [255, 140, 0],
      getRadius: 3600,
      pickable: true,
    }),
  ];

  const [viewLayers, layersViewState] = useState(layers);

  const updateLayers = () => {
    let newLayers = [...layers];
    newLayers.getRadius = getSize(viewState.zoom);
  };

  return (
    <div>
      <DeckGL
        layers={viewLayers}
        viewState={viewState}
        controller={true}
        getTooltip={({ object }) =>
          object && `${object.fromName} to ${object.toName}`
        }
        onViewStateChange={(v) => {
          updateViewState(v.viewState);
          updateLayers();
        }}
      >
        <StaticMap
          reuseMaps
          mapStyle={MAP_STYLE}
          preventStyleDiffing={true}
          mapboxApiAccessToken="pk.eyJ1IjoiY29ubmVjdGluZ3dvcmxkIiwiYSI6ImNrd2l3anlzdDB3bTAycG1kYXVlYnZtaXAifQ.fsAb70tSq-vGoIwjXuqreg"
        />
      </DeckGL>
    </div>
  );
};

export default Map;
