import   UserTable from  "../data/usersTable.json";
let nodes=["pranesh@gmail.com","vamshi@gmail.com","mahesh@gmail.com","virat@gmail.com","rohit@gmail.com","tarak@gmail.com","charan@gmail.com"];
let adjList=[
    [3,5,1],
    [0],
    [3,6],
    [2,4,0],
    [3],
    [6,0],
    [5,2]
];
let v=nodes.length;
function printAllPaths(s,d)
{
     let isVisited = new Array(v);
     for(let i=0;i<v;i++)
         isVisited[i]=false;
        let pathList = [];
  
        // add source to path[]
        pathList.push(nodes[s]);
        let pathLists=[]
        // Call recursive utility
        pathLists.push(printAllPathsUtil(s, d, isVisited, pathList));
        return pathLists;
}
 

function printAllPathsUtil(u,d,isVisited,localPathList)
{
    if (u == (d)) {
            //document.write(localPathList+"<br>");
            // if match found then no need to
            // traverse more till depth
            return localPathList;
        }
  
        // Mark the current node
        isVisited[u] = true;
  
        // Recur for all the vertices
        // adjacent to current vertex
        for (let i=0;i< adjList[u].length;i++) {
            if (!isVisited[adjList[u][i]]) {
                // store current node
                // in path[]
                localPathList.push(nodes[adjList[u][i]]);
                printAllPathsUtil(adjList[u][i], d,
                isVisited, localPathList);
  
                // remove current node
                // in path[]
                localPathList.splice(localPathList.indexOf
                (nodes[adjList[u][i]]),1);
            }
        }
        isVisited[u] = false;
}

const GITlist = (source,destination) => {
    console.log(printAllPaths(nodes.indexOf(source),nodes.indexOf(destination)));
    return (  
        printAllPaths(nodes.indexOf(source),nodes.indexOf(destination))
    );
}

export default GITlist;
