import React from "react";
import UsersTable from "../data/usersTable.json";
import "../stylesheets/searchBox.css";
class SearchBox extends React.Component {
  state = {
    searchResults: UsersTable,
    searchQuery: "",
  };

  updateSearchResults = () => {
    let newUsersTable = UsersTable.filter((user) => {
      return user.name
        .toLowerCase()
        .includes(this.state.searchQuery.toLowerCase());
    });
    let newState = { ...this.state };
    newState.searchResults = newUsersTable;
    this.setState(newState);
  };

  render() {
    return (
      <div className="SearchBoxMainDiv">
        <input
          type="text"
          className="SBInput"
          placeholder="&#128269; Search"
          onChange={(e) => {
            let newState = { ...this.state };
            newState.searchQuery = e.target.value.trim();
            this.setState(newState);
            setTimeout(() => {
              this.updateSearchResults();
            }, 1);
          }}
        />
        <div className="SBResultsDiv">
          {this.state.searchResults.map((user) => {
            let index = this.state.searchResults.indexOf(user);
            return (
              <div
                key={index}
                className={
                  "SBSearchResult" + index === 0
                    ? " SBFirstResult"
                    : "" + index === this.state.searchResults.length - 1
                    ? " SBLastResult"
                    : ""
                }
              >
                <p>{user.name}</p>
                <button className="SBAddFriendButton">Add Friend</button>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default SearchBox;
