let INITIAL_VIEW_STATE = {
  longitude: 78.4867,
  latitude: 17.385,
  zoom: 5,
  maxZoom: 20,
  pitch: 30,
  bearing: 0,
};

const MAP_STYLE = DarkMap;

const updateLayers = (zoom) => {
  console.log("Hi");
};

class Map extends React.Component {
  state = {
    layers: [
      new LineLayer({
        id: "friendship",
        data: friendshipData,
        opacity: 0.8,
        getSourcePosition: (d) => d.start,
        getTargetPosition: (d) => d.end,
        getColor: [225, 0, 0, 123],
        getWidth: 5,
        pickable: true,
      }),
      new ScatterplotLayer({
        id: "person",
        data: peopleData,
        radiusScale: 10,
        getPosition: (d) => d.coordinates,
        getFillColor: [255, 140, 0],
        getRadius: 3000,
        pickable: true,
      }),
    ],
  };

  render() {
    return (
      <div>
        <DeckGL
          layers={this.state.layers}
          initialViewState={INITIAL_VIEW_STATE}
          controller={true}
          getTooltip={({ object }) =>
            object && `${object.fromName} to ${object.toName}`
          }
          onViewStateChange={(v) => {
            console.log(v.viewState.zoom);
            updateLayers(v.viewState.zoom);
          }}
        >
          <StaticMap
            reuseMaps
            mapStyle={MAP_STYLE}
            preventStyleDiffing={true}
            mapboxApiAccessToken="pk.eyJ1IjoiY29ubmVjdGluZ3dvcmxkIiwiYSI6ImNrd2l3anlzdDB3bTAycG1kYXVlYnZtaXAifQ.fsAb70tSq-vGoIwjXuqreg"
          />
        </DeckGL>
      </div>
    );
  }
}

export default Map;
